import React from 'react';
import { useApi } from '../hooks/useApi';
import { getPedidos, deletarPedido } from '../services/api';

const Pedidos = () => {
    const { data: pedidos, loading, error, refetch } = useApi(getPedidos);

    if (loading) {
        return (
            <div className="container-fluid">
                <div className="d-flex justify-content-center">
                    <div className="spinner-border" role="status">
                        <span className="sr-only">Carregando...</span>
                    </div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="container-fluid">
                <div className="alert alert-danger" role="alert">
                    Erro ao carregar pedidos: {error}
                    <button className="btn btn-outline-danger btn-sm ml-2" onClick={refetch}>
                        Tentar novamente
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="container-fluid">
            <div className="d-sm-flex align-items-center justify-content-between mb-4">
                <h1 className="h3 mb-0 text-gray-800">Lista de Pedidos</h1>
                <button 
                    className="btn btn-primary btn-sm"
                    onClick={refetch}
                >
                    <i className="fas fa-sync-alt mr-1"></i>
                    Atualizar
                </button>
            </div>

            <div className="card shadow mb-4">
                <div className="card-header py-3">
                    <h6 className="m-0 font-weight-bold text-primary">Pedidos</h6>
                </div>
                <div className="card-body">
                    {pedidos && pedidos.length > 0 ? (
                        <div className="table-responsive">
                            <table className="table table-bordered" width="100%" cellSpacing="0">
                                <thead>
                                    <tr>
                                        <th>ID</th>
                                        <th>Cliente</th>
                                        <th>Status</th>
                                        <th>Total</th>
                                        <th>Data</th>
                                        <th>Ações</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {pedidos.map(pedido => (
                                        <tr key={pedido.id}>
                                            <td>{pedido.id}</td>
                                            <td>{pedido.cliente_nome}</td>
                                            <td>
                                                <span className={`badge badge-${
                                                    pedido.status === 'pendente' ? 'warning' :
                                                    pedido.status === 'preparo' ? 'info' :
                                                    pedido.status === 'entrega' ? 'primary' :
                                                    'success'
                                                }`}>
                                                    {pedido.status}
                                                </span>
                                            </td>
                                            <td>R$ {parseFloat(pedido.total || 0).toFixed(2)}</td>
                                            <td>{new Date(pedido.data_pedido).toLocaleDateString()}</td>
                                            <td>
                                                <button className="btn btn-info btn-sm mr-1">
                                                    <i className="fas fa-eye"></i>
                                                </button>
                                                <button className="btn btn-warning btn-sm">
                                                    <i className="fas fa-edit"></i>
                                                </button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    ) : (
                        <div className="text-center">
                            <p>Nenhum pedido encontrado.</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Pedidos;
