import axios from 'axios';

// Configuração da API - função que retorna a URL baseada no ambiente atual
function getApiUrl() {
    // Se estiver rodando no Cordova (protocolo file:), usar o servidor local
    if (typeof window !== 'undefined' && window.location.protocol === 'file:') {
        return 'http://localhost:3001';
    }

    // Detectar o ambiente baseado no hostname - v2.1
    if (typeof window !== 'undefined') {
        const hostname = window.location.hostname;
        const isLocal = hostname === 'localhost' || hostname === '127.0.0.1';

        if (isLocal) {
            // Em desenvolvimento local, usar o servidor local
            return 'http://localhost:3001';
        } else if (hostname === 'rotaexpress.free.nf') {
            // Em produção no InfinityFree, usar a API local
            return 'https://rotaexpress.free.nf/api';
        }
    }

    // Outros ambientes de produção (Vercel, etc.) ou fallback
    return 'https://appatlas-afusc3hg5-ayslanoons-projects.vercel.app/api';
}

// Configuração do axios
const api = axios.create({
    timeout: 10000,
    headers: {
        'Content-Type': 'application/json',
    }
});

// Interceptor para definir a baseURL dinamicamente
api.interceptors.request.use((config) => {
    const API_URL = getApiUrl();
    config.baseURL = API_URL;

    // Debug: Log da configuração da API
    console.log('API_URL configurado:', API_URL);
    if (typeof window !== 'undefined') {
        console.log('Protocolo atual:', window.location.protocol);
        console.log('Hostname atual:', window.location.hostname);
        console.log('Ambiente detectado:', window.location.protocol === 'file:' ? 'Cordova' : 'Web');
    }
    console.log('Build timestamp:', new Date().toISOString());

    return config;
});

// Função para testar a conexão com a API
export const testarConexao = async () => {
    try {
        await api.get('/produtos');
        console.log('Conexão com a API estabelecida com sucesso!');
        return true;
    } catch (error) {
        console.error('Erro de conexão:', error);
        throw new Error('Erro ao conectar com o servidor. Verifique se a API está rodando.');
    }
};

// Funções para gerenciamento de produtos
export const getProdutos = async () => {
    try {
        const response = await api.get('/produtos');
        return response.data;
    } catch (error) {
        console.error('Erro ao buscar produtos:', error);
        throw error;
    }
};

export const criarProduto = async (dados) => {
    try {
        const response = await api.post('/produtos', dados);
        return response.data;
    } catch (error) {
        console.error('Erro ao criar produto:', error);
        throw error;
    }
};

export const atualizarProduto = async (id, dados) => {
    try {
        const response = await api.put(`/produtos/${id}`, dados);
        return response.data;
    } catch (error) {
        console.error('Erro ao atualizar produto:', error);
        throw error;
    }
};

export const deletarProduto = async (id) => {
    try {
        const response = await api.delete(`/produtos/${id}`);
        return response.data;
    } catch (error) {
        console.error('Erro ao deletar produto:', error);
        throw error;
    }
};

// Funções para gerenciamento de pedidos
export const getPedidos = async () => {
    try {
        console.log('Iniciando busca de pedidos...');
        console.log('URL base configurada:', getApiUrl());
        const response = await api.get('/pedidos');
        console.log('Resposta recebida:', response);
        return response.data;
    } catch (error) {
        console.error('Erro ao buscar pedidos:', error);
        console.error('Detalhes do erro:', {
            message: error.message,
            status: error.response?.status,
            statusText: error.response?.statusText,
            url: error.config?.url,
            baseURL: error.config?.baseURL
        });
        throw error;
    }
};

export const getPedidoById = async (id) => {
    try {
        const response = await api.get(`/pedidos/${id}`);
        return response.data;
    } catch (error) {
        console.error('Erro ao buscar pedido:', error);
        throw error;
    }
};

export const criarPedido = async (pedido) => {
    try {
        const response = await api.post('/pedidos', pedido);
        return response.data;
    } catch (error) {
        console.error('Erro ao criar pedido:', error);
        throw error;
    }
};

export const atualizarPedido = async (id, dados) => {
    try {
        const response = await api.put(`/pedidos/${id}`, dados);
        return response.data;
    } catch (error) {
        console.error('Erro ao atualizar pedido:', error);
        throw error;
    }
};

export const deletarPedido = async (id) => {
    try {
        const response = await api.delete(`/pedidos/${id}`);
        return response.data;
    } catch (error) {
        console.error('Erro ao deletar pedido:', error);
        throw error;
    }
};

export const deletarTodosPedidos = async () => {
    try {
        const response = await api.delete('/pedidos?deleteAll=true');
        return response.data;
    } catch (error) {
        console.error('Erro ao deletar todos os pedidos:', error);
        throw error;
    }
};

export const getCardapio = async (tipo = '') => {
    try {
        const url = tipo ? `/cardapio?tipo=${tipo}` : '/cardapio';
        const response = await api.get(url);
        return response.data;
    } catch (error) {
        console.error('Erro ao buscar cardápio:', error);
        throw error;
    }
};

export default api;
