import React, { useState, useEffect } from 'react';
import { useApi } from '../hooks/useApi';
import { getPedidos } from '../services/api';

const Dashboard = () => {
    const { data: pedidos, loading, error, refetch } = useApi(getPedidos);
    const [stats, setStats] = useState({
        pendentes: 0,
        preparo: 0,
        entrega: 0,
        entregues: 0,
        vendasHoje: 0,
        ticketMedio: 0
    });

    useEffect(() => {
        if (pedidos) {
            calculateStats(pedidos);
        }
    }, [pedidos]);

    const calculateStats = (pedidosData) => {
        const hoje = new Date().toDateString();
        const pedidosHoje = pedidosData.filter(pedido => 
            new Date(pedido.data_pedido).toDateString() === hoje
        );

        const pendentes = pedidosData.filter(p => p.status === 'pendente').length;
        const preparo = pedidosData.filter(p => p.status === 'preparo').length;
        const entrega = pedidosData.filter(p => p.status === 'entrega').length;
        const entregues = pedidosHoje.filter(p => p.status === 'entregue').length;

        const vendasHoje = pedidosHoje
            .filter(p => p.status === 'entregue')
            .reduce((total, pedido) => total + parseFloat(pedido.total || 0), 0);

        const ticketMedio = entregues > 0 ? vendasHoje / entregues : 0;

        setStats({
            pendentes,
            preparo,
            entrega,
            entregues,
            vendasHoje,
            ticketMedio
        });
    };

    const formatCurrency = (value) => {
        return new Intl.NumberFormat('pt-BR', {
            style: 'currency',
            currency: 'BRL'
        }).format(value);
    };

    const handleRefresh = () => {
        refetch();
    };

    if (loading) {
        return (
            <div className="container-fluid">
                <div className="d-flex justify-content-center">
                    <div className="spinner-border" role="status">
                        <span className="sr-only">Carregando...</span>
                    </div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="container-fluid">
                <div className="alert alert-danger" role="alert">
                    Erro ao carregar dados: {error}
                    <button className="btn btn-outline-danger btn-sm ml-2" onClick={handleRefresh}>
                        Tentar novamente
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="container-fluid">
            {/* Page Heading */}
            <div className="d-sm-flex align-items-center justify-content-between mb-4">
                <h1 className="h3 mb-0 text-gray-800">Dashboard</h1>
                <button 
                    className="btn btn-primary btn-sm"
                    onClick={handleRefresh}
                >
                    <i className="fas fa-sync-alt mr-1"></i>
                    Atualizar
                </button>
            </div>

            {/* Content Row */}
