import React, { useState } from 'react';
import useBluetoothPrinter from '../hooks/useBluetoothPrinter';
import './BluetoothPrinterManager.css';

const BluetoothPrinterManager = ({ onPrintOrder, onPrintKitchen, showOrderButtons = true }) => {
  const {
    isConnected,
    isConnecting,
    connectedDevice,
    error,
    isBluetoothAvailable,
    connect,
    disconnect,
    printTest,
    clearError
  } = useBluetoothPrinter();

  const [showManager, setShowManager] = useState(false);

  const handleConnect = async () => {
    const success = await connect();
    if (success) {
      // Opcional: executar teste de impress√£o ap√≥s conectar
      // await printTest();
    }
  };

  const handleDisconnect = async () => {
    await disconnect();
  };

  const handleTest = async () => {
    await printTest();
  };

  if (!isBluetoothAvailable) {
    return (
      <div className="bluetooth-manager-unavailable">
        <span>‚ö†Ô∏è Bluetooth n√£o dispon√≠vel</span>
      </div>
    );
  }

  return (
    <div className="bluetooth-manager">
      {/* Bot√£o para mostrar/ocultar o gerenciador */}
      <button 
        className={`bluetooth-toggle ${isConnected ? 'connected' : 'disconnected'}`}
        onClick={() => setShowManager(!showManager)}
        title={isConnected ? `Conectado: ${connectedDevice?.name || 'Impressora'}` : 'Impressora Bluetooth'}
      >
        üñ®Ô∏è {isConnected ? '‚úÖ' : '‚ùå'}
      </button>

      {/* Painel do gerenciador */}
      {showManager && (
        <div className="bluetooth-panel">
          <div className="bluetooth-header">
            <h4>Impressora Bluetooth</h4>
            <button 
              className="close-btn"
              onClick={() => setShowManager(false)}
            >
              ‚úï
            </button>
          </div>

          <div className="bluetooth-content">
            {/* Status da conex√£o */}
            <div className="connection-status">
              <span className={`status-indicator ${isConnected ? 'connected' : 'disconnected'}`}>
                {isConnected ? 'üü¢ Conectado' : 'üî¥ Desconectado'}
              </span>
              {connectedDevice && (
                <span className="device-name">{connectedDevice.name}</span>
              )}
            </div>

            {/* Erro */}
            {error && (
              <div className="error-message">
                <span>‚ö†Ô∏è {error}</span>
                <button onClick={clearError} className="clear-error">‚úï</button>
              </div>
            )}

            {/* Dicas para o usu√°rio */}
            {!isConnected && !error && (
              <div className="help-tips">
                <h5>üí° Dicas para conectar:</h5>
                <ul>
                  <li>Certifique-se que a impressora est√° ligada</li>
                  <li>Verifique se o Bluetooth est√° ativado</li>
                  <li>A impressora deve estar pr√≥xima (at√© 10m)</li>
                  <li>Alguns navegadores podem solicitar permiss√£o</li>
                </ul>
              </div>
            )}

            {/* Bot√µes de controle */}
            <div className="control-buttons">
              {!isConnected ? (
                <button 
                  onClick={handleConnect}
                  disabled={isConnecting}
                  className="connect-btn"
                >
                  {isConnecting ? 'üîÑ Conectando...' : 'üîó Conectar'}
                </button>
              ) : (
                <button 
                  onClick={handleDisconnect}
                  className="disconnect-btn"
                >
                  üîå Desconectar
                </button>
              )}

              {isConnected && (
                <button 
                  onClick={handleTest}
                  className="test-btn"
                >
                  üß™ Teste
                </button>
              )}
            </div>

            {/* Bot√µes de impress√£o de pedidos (se habilitado) */}
            {showOrderButtons && isConnected && (
              <div className="print-buttons">
                <h5>Impress√£o R√°pida:</h5>
                <button 
                  onClick={onPrintOrder}
                  className="print-order-btn"
                  disabled={!onPrintOrder}
                >
                  üìÑ Imprimir Pedido
                </button>
                <button 
                  onClick={onPrintKitchen}
                  className="print-kitchen-btn"
                  disabled={!onPrintKitchen}
                >
                  üë®‚Äçüç≥ Recibo Cozinha
                </button>
              </div>
            )}

            {/* Instru√ß√µes */}
            <div className="instructions">
              <small>
                üí° <strong>Dica:</strong> Certifique-se de que sua impressora POS58 
                est√° ligada e em modo de pareamento antes de conectar.
              </small>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BluetoothPrinterManager;
